import React, { Component } from 'react';

class Question extends Component{
  render(){
    return (
    <div>
      <fieldset id="Question number" class="center bg-light-blue br2 pa2 w-75">
        <legend class="fw7 mb2">Question X</legend>
        <div class="flex items-center ma4">
          <input  type="radio" id="A1" name="Q1" value="a1" />
          <label htmlFor="spacejam" class="lh-copy">Space Jam</label>
        </div>
        <div class="flex items-center ma4">
          <input  type="radio" id="A1" name="Q1" value="a2" />
          <label htmlFor="airbud" class="lh-copy">Air Bud</label>
        </div>
        <div class="flex items-center ma4">
          <input  type="radio" id="A1" name="Q1" value="a3" />
          <label htmlFor="hocuspocus" class="lh-copy">Hocus Pocus</label>
        </div>
        <div class="flex items-center ma4">
          <input  type="radio" id="A1" name="Q1" value="a4" />
          <label htmlFor="diehard" class="lh-copy">Die Hard</label>
        </div>
      </fieldset>
    </div>
  );
  }  
}

export default Question;
