import React, { Component } from 'react';

class Question extends Component{
  render(){
    const {id, prompt, options} = this.props
    return (
    <div>
      <fieldset id={id} class="ma2 bn center flex flex-column bg-lightest-blue br2 pa2 w-75">
        <legend class="fw7 mb2 pb3">Question {id}</legend>
        <p> {prompt} </p>
        {
        Object.keys(options).forEach((key) => {
        return (
             <div class="flex items-center ma2">
               <input  type="radio" id={key} name={id} value={key} />
               <label htmlFor={key} class="lh-copy">{options[key]}</label>
             </div>
            )
          })
        }
      </fieldset>
    </div>
  );
  }  
}

export default Question;

