import React, { Component } from 'react';

class Question extends Component{
  render(){
    return (
    <div>
      <fieldset id={this.props.id} class="ma2 bn center flex flex-column bg-lightest-blue br2 pa2 w-75">
        <legend class="fw7 mb2 pb3">Question {this.props.id}</legend>
        <p> {this.props.prompt} </p>
        {
        this.props.options.map(([id, option]) => {
        return (
             <div class="flex items-center ma2">
               <input  type="radio" id={id} name={this.props.id} value={id} />
               <label htmlFor={id} class="lh-copy">{option}</label>
             </div>
            )
          })
        }
      </fieldset>
    </div>
  );
  }  
}

export default Question;

